import React from 'react';
import {Routes, Route, redirect} from 'react-router-dom'
import {ADMIN_ROUTE, BASKET_ROUTE, DEVICE_ROUTE, LOGIN_ROUTE, REGISTRATION_ROUTE, SHOP_ROUTE} from "../utils/consts";
import Admin from "../pages/Admin";
import Basket from "../pages/Basket";
import Shop from "../pages/Shop";
import DevicePage from "../pages/DevicePage";
import auth from "../pages/Auth";

const AppRouter = () => {
    const isAuth = false
    return (
        <Routes>
            if (isAuth) {
            <Route key = {ADMIN_ROUTE} element={Admin} path = {ADMIN_ROUTE} exact />}
            if(isAuth){
            <Route key = {BASKET_ROUTE} element={Basket} path = {BASKET_ROUTE} exact />
        }
            <Route key = {SHOP_ROUTE} element={Shop} path={SHOP_ROUTE} exact />
            <Route key = {DEVICE_ROUTE} element={DevicePage} path = {DEVICE_ROUTE} exact />
            <Route key = {LOGIN_ROUTE} element={auth} path = {LOGIN_ROUTE} exact />
            <Route key = {REGISTRATION_ROUTE} element={auth} path = {REGISTRATION_ROUTE} exact />

            <redirect to={SHOP_ROUTE} />
            </Routes>
    );
};

export default AppRouter;
