import React, {useContext} from 'react';
import Modal from "react-bootstrap/Modal";
import {Button, Dropdown, Form} from "react-bootstrap";
import {Context} from "../../index";

const CreateDevice = ({show,onHide}) => {

    const {device} = useContext(Context)

    return (
        <Modal size="lg" centered show={show} onHide={onHide}>
            <Modal.Dialog>
                <Modal.Header className="align-self-center" closeButton>
                    <Modal.Title>Add new device</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <Form>
                        <Dropdown>
                        <Dropdown.Toggle>Choose your type</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {device.types.map(type =>
                            <Dropdown.Item key={type.id}>{type.name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                        </Dropdown>
                        <Dropdown>
                            <Dropdown.Toggle>Choose your brand</Dropdown.Toggle>
                            <Dropdown.Menu>
                                {device.brands.map(brand =>
                                    <Dropdown.Item key={brand.id}>{brand.name}</Dropdown.Item>
                                )}
                            </Dropdown.Menu>
                        </Dropdown>
                    </Form>
                </Modal.Body>

                <Modal.Footer>
                    <div className='justify-content-start'>
                        <Button className='mr-1' onClick={onHide} variant="outline-danger">Close</Button>
                        <Button onClick={onHide} variant="outline-success">Add</Button>
                    </div>
                </Modal.Footer>
            </Modal.Dialog>
        </Modal>
    );
};

export default CreateDevice;
