import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor() {
        this._types = [
            {
               id:1, name:"Fridges"
            },
            {
              id:2, name:"Smartphones"
            }
        ]
        this._brands = [
            {id:1,name:"Samsung"},
            {id:2, name:"Apple"}
        ]
        this._devices = [
            {id:1,name:"Iphone 12", price:1000,rating:5,img:""},
            {id:2,name:"Iphone 13", price:1100,rating:5,img:""}
        ]
        this._selectedType = {}
        makeAutoObservable(this)
    }
    setSelectedType(type){
        this._selectedType = type
    }
    setTypes(types){
        this._types = types
    }
    setBrands(brands){
        this._brands = brands
    }
    setDevices(devices){
        this._devices = devices
    }

    get selectedType(){
        return this._selectedType
    }
    get devices(){
        return this._devices
    }
    get brands(){
        return this._brands
    }
    get types(){
        return this._types
    }
}
