import React from 'react';
import Modal from "react-bootstrap/Modal";
import {Button, Form} from "react-bootstrap";

const CreateDevice = ({show,onHide}) => {
    return (
        <Modal size="lg" centered show={show} onHide={onHide}>
            <Modal.Dialog>
                <Modal.Header className="align-self-center" closeButton>
                    <Modal.Title>Add new device</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <Form>
                        <Form.Control placeholder = {"Enter the device name"} />
                    </Form>
                </Modal.Body>

                <Modal.Footer>
                    <div className='justify-content-start'>
                        <Button className='mr-1' onClick={onHide} variant="outline-danger">Close</Button>
                        <Button onClick={onHide} variant="outline-success">Add</Button>
                    </div>
                </Modal.Footer>
            </Modal.Dialog>
        </Modal>
    );
};

export default CreateDevice;
