import React, {useContext} from 'react';
import {Routes, Route, Navigate} from 'react-router-dom'
import {ADMIN_ROUTE, BASKET_ROUTE, DEVICE_ROUTE, LOGIN_ROUTE, REGISTRATION_ROUTE, SHOP_ROUTE} from "../utils/consts";
import Admin from "../pages/Admin";
import Basket from "../pages/Basket";
import Shop from "../pages/Shop";
import DevicePage from "../pages/DevicePage";
import Auth from "../pages/Auth";
import {Context} from "../index";

const AppRouter = () => {
    const {user} = useContext(Context)

    console.log(user)
    return (
        <Routes>
            <Route path = {SHOP_ROUTE} exact element={<Shop/>} />
            <Route path = {DEVICE_ROUTE} exact element={<DevicePage/>} />
            <Route path = {LOGIN_ROUTE} exact element={<Auth />} />
            <Route path = {REGISTRATION_ROUTE} exact element={<Auth />} />
                <Route path = {ADMIN_ROUTE} exact element={user.isAuth ?  <Admin /> : <Navigate replace to='/'/>} />
                <Route path = {BASKET_ROUTE} exact element={user.isAuth ?  <Basket /> : <Navigate replace to='/'/>} />
            <Route path = '*' element={<Navigate to="/" replace/>} />
            </Routes>
    );
};

export default AppRouter;
